import React, { useState, useEffect } from 'react';
import './MemoryGame.css';

const MemoryGame = () => {
    const [gameStarted, setGameStarted] = useState(false);
    const [flippedCards, setFlippedCards] = useState(0);
    const [totalFlips, setTotalFlips] = useState(0);
    const [totalTime, setTotalTime] = useState(0);
    const [loop, setLoop] = useState(null);

    const generateGame = () => {
        const dimensions = 4; // Adjust as needed

        if (dimensions % 2 !== 0) {
            throw new Error("The dimension of the board must be an even number.");
        }

        const emojis = ['ðŸ', 'ðŸŠ', 'ðŸ‹', 'ðŸ‰', 'ðŸ‡', 'ðŸ“', 'ðŸ¥‘', 'ðŸ¥•', 'ðŸŒ½', 'ðŸ¥¬'];
        const picks = pickRandom(emojis, (dimensions * dimensions) / 2);
        const items = shuffle([...picks, ...picks]);
        const cards = items.map((item, index) => (
            <div className="card" key={index}>
                <div className="card-front"></div>
                <div className="card-back">{item}</div>
            </div>
        ));

        return (
            <div className="board" style={{ gridTemplateColumns: `repeat(${dimensions}, auto)` }}>
                {cards}
            </div>
        );
    };

    useEffect(() => {
        let interval;

        const startGame = () => {
            setGameStarted(true);
            const newLoop = setInterval(() => {
                setTotalTime(prevTime => prevTime + 1);
                setTotalFlips(flips => flips + 1);
            }, 1000);
            setLoop(newLoop);
        };

        const endGame = () => {
            clearInterval(loop);
            setTimeout(() => {
                resetGame();
            }, 5000);
        };

        const resetCards = () => {
            document.querySelectorAll('.card').forEach(card => {
                card.classList.remove('flipped', 'matched');
            });
        };

        const resetGame = () => {
            setGameStarted(false);
            setFlippedCards(0);
            setTotalFlips(0);
            setTotalTime(0);

            clearInterval(loop);
            resetCards();
        };

        const flipBackCards = () => {
            document.querySelectorAll('.card:not(.matched)').forEach(card => {
                card.classList.remove('flipped');
            });
            setFlippedCards(0);
        };

        const flipCard = card => {
            setFlippedCards(prevFlips => prevFlips + 1);
            setTotalFlips(prevFlips => prevFlips + 1);

            if (!gameStarted) {
                startGame();
            }
            if (flippedCards <= 2) {
                card.classList.add('flipped');
            }
            if (flippedCards === 2) {
                const flippedCards = document.querySelectorAll('.flipped:not(.matched)');
                if (flippedCards[0].innerText === flippedCards[1].innerText) {
                    flippedCards[0].classList.add('matched');
                    flippedCards[1].classList.add('matched');
                }

                setTimeout(() => {
                    flipBackCards();
                }, 1000);
            }
            if (!document.querySelectorAll('.card:not(.flipped)').length) {
                setTimeout(() => {
                    document.querySelector('.win').innerHTML = `
                        <span class="win-text">
                            You won!<br />
                            with <span class="highlight">${totalFlips}</span> moves<br />
                            under <span class="highlight">${totalTime}</span> seconds
                        </span>
                    `;
                    clearInterval(loop);
                    setTimeout(() => {
                        endGame();
                    }, 1000);
                }, 1000);
            }
        };

        const attachEventListeners = () => {
            document.querySelector('.start-button').addEventListener('click', event => {
                resetGame();
                startGame();
            });

            document.addEventListener('click', event => {
                const eventTarget = event.target;
                const eventParent = eventTarget.parentElement;

                if (eventTarget.className.includes('card') && !eventParent.classList.contains('flipped')) {
                    flipCard(eventParent);
                }
            });
        };

        generateGame();
        attachEventListeners();

        return () => {
            clearInterval(loop);
        };
    }, []);

    const shuffle = array => {
        const clonedArray = [...array];

        for (let i = clonedArray.length - 1; i > 0; i--) {
            const randomIndex = Math.floor(Math.random() * (i + 1));
            const original = clonedArray[i];

            clonedArray[i] = clonedArray[randomIndex];
            clonedArray[randomIndex] = original;
        }
        return clonedArray;
    };

    const pickRandom = (array, items) => {
        const clonedArray = [...array];
        const randomPicks = [];

        for (let i = 0; i < items; i++) {
            const randomIndex = Math.floor(Math.random() * clonedArray.length);

            randomPicks.push(clonedArray[randomIndex]);
            clonedArray.splice(randomIndex, 1);
        }
        return randomPicks;
    };

    return (
        <div className='MG-con'>
        <div className="container">
            <div className="start-container">
                <button className="start-button">Start</button>
            </div>
            <div className="GAME">
                <div className="CONTROLS">
                    <div className="stats">
                        <div className="moves">{totalFlips} moves</div>
                        <div className="timer">Time: {totalTime} sec</div>
                    </div>
                </div>
                <div className="board_container">{generateGame()}</div>

                <div className="win"></div>
            </div>
        </div>
        </div>
    );
};

export default MemoryGame;
