import React, { useState, useEffect } from 'react';
import './ReactionTime.css';

const reactionTime = () => {
    const [greenDisplayed, setGreenDisplayed] = useState(false);
    const [waitingForStart, setWaitingForStart] = useState(true);
    const [waitingForGreen, setWaitingForGreen] = useState(false);
    const [scores, setScores] = useState([]);
    const [reactionTime, setReactionTime] = useState(0);
  
    useEffect(() => {
      if (waitingForGreen) {
        setGreenDisplayed(true);
        const timeout = setTimeout(() => {
          setReactionTime(Date.now());
          setGreenDisplayed(false);
        }, Math.floor(Math.random() * 4000 + 3000));
        return () => clearTimeout(timeout);
      }
    }, [waitingForGreen]);
  
    const startGame = () => {
      setWaitingForStart(false);
      setWaitingForGreen(true);
    };
  
    const displayReactionTime = () => {
      const clickTime = Date.now();
      const rt = clickTime - reactionTime;
      setScores([...scores, rt]);
      if (scores.length >= 2) {
        endGame();
      } else {
        setWaitingForGreen(true);
      }
    };
  
    const endGame = () => {
      let total = 0;
      scores.forEach((s) => {
        total += s;
      });
      const averageScore = Math.round(total / scores.length);
      alert(`Your average reaction time: ${averageScore} ms`);
      setScores([]);
      setWaitingForStart(true);
    };
  
    const handleClick = () => {
      if (greenDisplayed) {
        displayReactionTime();
      } else if (waitingForStart) {
        startGame();
      } else if (waitingForGreen) {
        alert('Too Soon');
      }
    };
  
    return (
      <div>
        <div className={`main-menu ${waitingForStart ? 'active' : ''}`}>
          <h1>Reaction Time Test</h1>
          <p>Click as soon as you see the green color on the screen.</p>
          <p>Click anywhere to start.</p>
        </div>
  
        <div
          className={`clickable-area ${greenDisplayed ? 'green' : ''}`}
          onClick={handleClick}
        >
          <div className="message">
            {greenDisplayed ? 'Click Now!' : 'Wait for the Green Color.'}
          </div>
        </div>
      </div>
    );
  };
  
  export default reactionTime;
