import React, { useState, useEffect } from 'react';
import './MemoryGame.css';

const MemoryGame = () => {
    const [gameStarted, setGameStarted] = useState(false);
    const [flippedCards, setFlippedCards] = useState([]);
    const [totalFlips, setTotalFlips] = useState(0);
    const [totalTime, setTotalTime] = useState(0);
    const [loop, setLoop] = useState(null);
    const [cards, setCards] = useState([]);
    
    const generateGame = () => {
        const dimensions = 4; // Adjust as needed

        if (dimensions % 2 !== 0) {
            throw new Error("The dimension of the board must be an even number.");
        }

        const emojis = ['ðŸ', 'ðŸŠ', 'ðŸ‹', 'ðŸ‰', 'ðŸ‡', 'ðŸ“', 'ðŸ¥‘', 'ðŸ¥•', 'ðŸŒ½', 'ðŸ¥¬'];
        const picks = pickRandom(emojis, (dimensions * dimensions) / 2);
        const items = shuffle([...picks, ...picks]);
        const newCards = items.map((item, index) => ({ id: index, emoji: item, flipped: false, matched: false }));

        setCards(newCards);
    };
    useEffect(() => {
        

        const startGame = () => {
            setGameStarted(true);
            const newLoop = setInterval(() => {
                setTotalTime(prevTime => prevTime + 1);
            }, 1000);
            setLoop(newLoop);
        };

        const endGame = () => {
            clearInterval(loop);
            setTimeout(() => {
                resetGame();
            }, 5000);
        };

        const resetGame = () => {
            setGameStarted(false);
            setFlippedCards([]);
            setTotalFlips(0);
            setTotalTime(0);
            clearInterval(loop);
            generateGame();
        };

        const flipCard = card => {
            if (!gameStarted) {
                startGame();
            }
            if (flippedCards.length < 2 && !card.flipped) {
                const newCards = cards.map(c =>
                    c.id === card.id ? { ...c, flipped: true } : c
                );
                setCards(newCards);
                setFlippedCards([...flippedCards, card]);
                setTotalFlips(totalFlips + 1);
            }
        };

        generateGame();

        return () => {
            clearInterval(loop);
        };
    }, []);

    useEffect(() => {
        if (flippedCards.length === 2) {
            const [firstCard, secondCard] = flippedCards;
            if (firstCard.emoji === secondCard.emoji) {
                const newCards = cards.map(c =>
                    c.id === firstCard.id || c.id === secondCard.id
                        ? { ...c, matched: true }
                        : c
                );
                setCards(newCards);
            }
            setTimeout(() => {
                const newCards = cards.map(c =>
                    c.id === firstCard.id || c.id === secondCard.id
                        ? { ...c, flipped: false }
                        : c
                );
                setCards(newCards);
                setFlippedCards([]);
            }, 1000);
        }
    }, [flippedCards]);

    const shuffle = array => {
        const clonedArray = [...array];

        for (let i = clonedArray.length - 1; i > 0; i--) {
            const randomIndex = Math.floor(Math.random() * (i + 1));
            const original = clonedArray[i];

            clonedArray[i] = clonedArray[randomIndex];
            clonedArray[randomIndex] = original;
        }
        return clonedArray;
    };

    const pickRandom = (array, items) => {
        const clonedArray = [...array];
        const randomPicks = [];

        for (let i = 0; i < items; i++) {
            const randomIndex = Math.floor(Math.random() * clonedArray.length);

            randomPicks.push(clonedArray[randomIndex]);
            clonedArray.splice(randomIndex, 1);
        }
        return randomPicks;
    };

    return (
        <div className='MG-con'>
        <div className="container">
            <div className="start-container">
                <button className="start-button">Start</button>
            </div>
            <div className="GAME">
                <div className="CONTROLS">
                    <div className="stats">
                        <div className="moves">{totalFlips} moves</div>
                        <div className="timer">Time: {totalTime} sec</div>
                    </div>
                </div>
                <div className="board_container">{generateGame()}</div>

                <div className="win"></div>
            </div>
        </div>
        </div>
    );
};

export default MemoryGame;
